contract_v3 = web3.eth.contract(address=unisocks_v3_pool, abi=unisocks_v3_abi)


def trim_address(address):
    return address[:6] + '...' + address[-4:]


# define function to handle events and print to the console
def handle_event(event):
    dictionary = json.loads(Web3.toJSON(event))
    tx = dictionary['transactionHash']
    amt0 = 0-(dictionary['args']['amount0'] / (10 ** 18))
    amt1 = (dictionary['args']['amount1'] / (10 ** 18))
    recipient = dictionary['args']['recipient']
    ens_rec = ns.name(recipient)
    recipient = ens_rec if ens_rec is not None else trim_address(recipient)

    if amt0 >= 0.01:
        post_text = '{0} has puchased {1} SOCKS for {2} ETH \n\nhttps://etherscan.io/tx/{3} \nhttps://unisocks.exchange/ \n#unisocks'.format(recipient, float(amt0), float(amt1), tx)
        print(post_text)
        print('')
        # Create a tweet
        api.update_status_with_media(filename='./resources/unisocks.png', status=post_text)


# asynchronous defined function to loop
# this loop sets up an event filter and is looking for new entries for the "Swap" event
# this loop runs on a poll interval
async def log_loop(event_filter, poll_interval):
    while True:
        for swap in event_filter.get_new_entries():
            handle_event(swap)
        await asyncio.sleep(poll_interval)


# when main is called
# create a filter for the latest block and look for the "Swap" event
# run an async loop
# try to run the log_loop function above every 2 seconds
def main():
    event_filter_v2 = contract_v2.events.Swap.createFilter(fromBlock='latest')
    event_filter_v3 = contract_v3.events.Swap.createFilter(fromBlock='latest')

    loop = asyncio.get_event_loop()
    try:
        loop.run_until_complete(
            asyncio.gather(
                log_loop(event_filter_v2, 2),
                log_loop(event_filter_v3, 2)
            )
        )
    finally:
        # close loop to free up system resources
        loop.close()


if __name__ == "__main__":
    print('running unisocks twitter bot')
    main()
